package com.android.workshop.helper;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.viewpager.widget.PagerAdapter;import com.android.workshop.R;public    class ViewPagerAdapter extends PagerAdapter {    private Context context;    @Override    public int getCount() {        return 0;    }    @Override    public boolean isViewFromObject(@NonNull View view, @NonNull Object object) {        return false;    }    @NonNull    @Override    public Object instantiateItem(@NonNull ViewGroup container, int position) {        LayoutInflater inflater = LayoutInflater.from(context);        ViewGroup viewGroup = (ViewGroup) inflater.inflate(R.layout.activity_main,container,false);        return viewGroup;    }    @Override    public void destroyItem(@NonNull ViewGroup container, int position, @NonNull Object object) {        super.destroyItem(container, position, object);    }    @Nullable    @Override    public CharSequence getPageTitle(int position) {        return super.getPageTitle(position);    }}